import React from 'react';
// import { Link } from 'react-router-dom';
import { Link, useLocation } from 'react-router-dom';

// const LoginRegisterOptions = ({ isLogin }) => {
//   const buttonText = isLogin ? 'Login' : 'Register';
//   const linkPath = isLogin ? '/Login' : '/Register';

const LoginRegisterOptions = () => {
    const location = useLocation();
    const isLogin = location.pathname === '/Login';  
    const linkPath = isLogin ? '/Login' : '/Register';
    const buttonText = isLogin ? 'Login' : 'Register';

  return (
    <div>
      <h2>{isLogin ? 'Login' : 'Register'}</h2>
      <div>
        <Link to="/StudentRegister">
          <button>Student</button>
        </Link>
        <Link to="/FacultyRegister">
          <button>Faculty</button>
        </Link>
        <Link to="/SecurityRegister">
          <button>Security</button>
        </Link>
        {!isLogin && ( // Conditionally render the button when isLogin is false
          <Link to={linkPath}>
            <button>{buttonText}</button>
          </Link>
        )}
      </div>
    </div>
  );
};

export default LoginRegisterOptions;
