import React, { useState, useEffect } from 'react';
import axios from 'axios';

function StudentPage({ match }) {
  const [studentInfo, setStudentInfo] = useState({
    name: '',
    rollNo: '',
    branch: '',
    section: '',
    semester: '',
    phoneNumber: '',
    personalEmail: '',
    officialEmail: '',
    parentName: '',
    parentPhoneNumber: '',
    parentEmail: '',
  });

  const [leaveForm, setLeaveForm] = useState({
    reason: '',
    startDate: '',
    endDate: '',
  });

  useEffect(() => {
    const { rollNo } = match.params;

    axios.get(`/api/students/${rollNo}`)
      .then((response) => {
        setStudentInfo(response.data);
      })
      .catch((error) => {
        console.error(error);
      });
  }, [match.params]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setLeaveForm({ ...leaveForm, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Send the leave application data to the server or perform actions as needed
    console.log(leaveForm);
  };

  const styles = {
    container: {
      maxWidth: '600px',
      margin: '0 auto',
      padding: '20px',
      border: '1px solid #ccc',
      borderRadius: '5px',
      boxShadow: '0 0 5px rgba(0, 0, 0, 0.2)',
    },
    heading: {
      textAlign: 'center',
      marginBottom: '20px',
    },
    infoList: {
      listStyleType: 'none',
      padding: '0',
    },
    infoListItem: {
      marginBottom: '10px',
    },
    form: {
      display: 'flex',
      flexDirection: 'column',
    },
    formGroup: {
      marginBottom: '20px',
    },
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Student Information</h2>
      <ul style={styles.infoList}>
        {Object.entries(studentInfo).map(([key, value]) => (
          <li key={key} style={styles.infoListItem}>
            <strong>{key}:</strong> {value}
          </li>
        ))}
      </ul>

      <h2 style={styles.heading}>Leave Application</h2>
      <form style={styles.form} onSubmit={handleSubmit}>
        <div style={styles.formGroup}>
          <label>
            Reason for Leave:
            <textarea
              name="reason"
              value={leaveForm.reason}
              onChange={handleInputChange}
              required
              rows="4"
            />
          </label>
        </div>
        <div style={styles.formGroup}>
          <label>
            Start Date:
            <input
              type="date"
              name="startDate"
              value={leaveForm.startDate}
              onChange={handleInputChange}
              required
            />
          </label>
        </div>
        <div style={styles.formGroup}>
          <label>
            End Date:
            <input
              type="date"
              name="endDate"
              value={leaveForm.endDate}
              onChange={handleInputChange}
              required
            />
          </label>
        </div>
        <div>
          <button type="submit">Submit Leave Application</button>
        </div>
      </form>
    </div>
  );
}

export default StudentPage;
