import React, { useState } from 'react';

function StudentPage() {
  // Student information
  const studentInfo = {
    name: 'John Doe',
    rollNo: '2023001',
    branch: 'CSE',
    section: 'A',
    semester: 'I',
    phoneNumber: '1234567890',
    personalEmail: 'john@example.com',
    officialEmail: 'john.doe@university.edu',
    parentName: 'Jane Doe',
    parentPhoneNumber: '9876543210',
    parentEmail: 'jane@example.com',
  };

  // State for leave application form
  const [leaveForm, setLeaveForm] = useState({
    reason: '',
    startDate: '',
    endDate: '',
  });

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setLeaveForm({ ...leaveForm, [name]: value });
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    // You can perform actions with the leave application data here
    console.log(leaveForm);
  };

  return (
    <div>
      <h2>Student Information</h2>
      <ul>
        {Object.entries(studentInfo).map(([key, value]) => (
          <li key={key}>
            <strong>{key}: </strong>
            {value}
          </li>
        ))}
      </ul>

      <h2>Leave Application</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>
            Reason for Leave:
            <input
              type="text"
              name="reason"
              value={leaveForm.reason}
              onChange={handleInputChange}
              required
            />
          </label>
        </div>
        <div>
          <label>
            Start Date:
            <input
              type="date"
              name="startDate"
              value={leaveForm.startDate}
              onChange={handleInputChange}
              required
            />
          </label>
        </div>
        <div>
          <label>
            End Date:
            <input
              type="date"
              name="endDate"
              value={leaveForm.endDate}
              onChange={handleInputChange}
              required
            />
          </label>
        </div>
        <div>
          <button type="submit">Submit Leave Application</button>
        </div>
      </form>
    </div>
  );
}

export default StudentPage;
